import React, { useCallback, useState } from 'react';
import { Drawer, Button, Input, Form } from 'antd';
import { UserOutlined, LockOutlined } from '@ant-design/icons';
import styled from 'styled-components';
import Link from 'next/link';
import { useDispatch, useSelector } from 'react-redux';

const ButtonWrapper = styled(Button)`
    background: #aacd6e;
    border-color: #aacd6e;
    
    :hover {
        background: #80c340;
        border-color: #80c340;
    }
`;

const InputWrapper = styled(Input)`    
    :hover {
        border-color: #aacd6e;
    } 
`;

const LoginForm = () => {
    // const dispatch = useDispatch();
    // const { logInLoading, logInError } = useSelector((state) => state.user);
    const [visible, setVisible] = useState(false);

    const showDrawer = () => {
        setVisible(true);
    };

    const onClose = () => {
        setVisible(false);
    };

    // useEffect(() => {
    //     if (logInError) {
    //       alert(logInError);
    //     }
    // }, [logInError]);

    const onSubmitForm = useCallback(() => {
        console.log(email, password);
        // dispatch({
        //   type: LOG_IN_REQUEST,
        //   data: { email, password },
        // });
      }, []);

    return (
        <>
        <ButtonWrapper type="primary" onClick={showDrawer}>
            로그인
        </ButtonWrapper>
        <Drawer
            title="로그인"
            placement="right"
            closable={false}
            onClose={onClose}
            visible={visible}
        >
            <Form onFinish={onSubmitForm}>
                <br />
                <InputWrapper placeholder="이메일" type="email"  prefix={<UserOutlined />} />
                <br />
                <br />
                <InputWrapper placeholder="비밀번호" type="password" prefix={<LockOutlined />} />
                <br />
                <ButtonWrapper type="primary" htmlType="submit">로그인</ButtonWrapper>
                <Link href="/"><a>회원가입</a></Link>
                <Link href="/"><a>비밀번호 찾기</a></Link>
            </Form>
        </Drawer>
        </>
    );
};

export default LoginForm;
