import React, { useState } from 'react';
import { Drawer, Button, Input } from 'antd';
import { UserOutlined, LockOutlined } from '@ant-design/icons';
import styled from 'styled-components';

const ButtonWrapper = styled(Button)`
    background: #aacd6e;
    border-color: #aacd6e;
    
    :hover {
        background: #80c340;
        border-color: #80c340;
    }
    
`;

const InputWrapper = styled(Input)`    
    :hover {
        border-color: #80c340;
    }
    
`;

const LoginForm = () => {
    const [visible, setVisible] = useState(false);

    const showDrawer = () => {
        setVisible(true);
    };

    const onClose = () => {
        setVisible(false);
    };

    return (
        <>
        <ButtonWrapper type="primary" onClick={showDrawer}>
            로그인
        </ButtonWrapper>
        <Drawer
            title="로그인"
            placement="right"
            closable={false}
            onClose={onClose}
            visible={visible}
        >
            <br />
            <InputWrapper placeholder="default size" prefix={<UserOutlined />} />
            <br />
            <br />
            <InputWrapper placeholder="default size" prefix={<LockOutlined />} />
        </Drawer>
        </>
    );
};

export default LoginForm;
