import React, { useCallback, useState } from 'react';
import { Drawer, Button, Input, Form } from 'antd';
import { UserOutlined, LockOutlined } from '@ant-design/icons';
import styled from 'styled-components';

const ButtonWrapper = styled(Button)`
    background: #aacd6e;
    border-color: #aacd6e;
    
    :hover {
        background: #80c340;
        border-color: #80c340;
    }
`;

const InputWrapper = styled(Input)`    
    :hover {
        border-color: #aacd6e;
    } 
`;

const LoginForm = () => {
    const [visible, setVisible] = useState(false);

    const showDrawer = () => {
        setVisible(true);
    };

    const onClose = () => {
        setVisible(false);
    };

    const onSubmitForm = useCallback(() => {
        console.log(email, password);
        // dispatch({
        //   type: LOG_IN_REQUEST,
        //   data: { email, password },
        // });
      }, []);

    return (
        <>
        <ButtonWrapper type="primary" onClick={showDrawer}>
            로그인
        </ButtonWrapper>
        <Drawer
            title="로그인"
            placement="right"
            closable={false}
            onClose={onClose}
            visible={visible}
        >
            <Form onFinish={onSubmitForm}>
                <br />
                <InputWrapper placeholder="이메일" type="email"  prefix={<UserOutlined />} />
                <br />
                <br />
                <InputWrapper placeholder="비밀번호" type="password" prefix={<LockOutlined />} />
                <ButtonWrapper>
                    <Button type="primary" htmlType="submit" loading={logInLoading}>로그인</Button>
                    <Link href="/signup"><a>회원가입</a></Link>
                    <Link href="/signup"><a>비밀번호 찾기</a></Link>
                </ButtonWrapper>
            </Form>
        </Drawer>
        </>
    );
};

export default LoginForm;
